                  @mousedown="startDrag($event, 'events')"
                  @touchstart="startDrag($event, 'events')"
                >
                  <div
                    class="modern-slide"
                    v-for="event in upcomingEvents"
                    :key="event.id"
                    :class="{ 'active-slide': isActiveSlide(event.id, 'events') }"
                  >
                    <div class="event-card">
