<script setup lang="ts">
import { ref, onMounted, onUnmounted, computed } from 'vue'

// Pagination
const page = ref(1)
const totalPages = ref(5)

// Slider controls
const eventsSlideOffset = ref(0)
const eventWidth = ref(316) // card width (300px) + gap (16px)
const eventsVisibleItems = ref(3) // Show 3 items at once for events

// Drag functionality
const dragStartX = ref(0)
const isDragging = ref(false)
const currentSlider = ref('')

// Filter data
const eventTypes = [
  'Все типы',
  'Мастер-класс',
  'Лекция',
  'Выставка',
  'Презентация',
  'Встреча с автором'
]

const locations = [
  'Все библиотеки',
  'Центральная городская библиотека',
  'Библиотека им. Абая',
  'Детская библиотека №3',
  'Национальная библиотека'
]

// Sample events data
const events = ref([
  {
    id: '1',
    title: 'Литературный вечер "Классики современности"',
    type: 'Мероприятие',
    time: '15 мая 2023, 18:00',
    location: 'Центральная библиотека',
    badge: 'Популярное'
  },
  {
    id: '2',
    title: 'Мастер-класс по каллиграфии',
    type: 'Мастер-класс',
    time: '20 мая 2023, 15:00',
    location: 'Библиотека №3',
    badge: 'Новое'
  },
  {
    id: '3',
    title: 'Выставка редких книг',
    type: 'Выставка',
    time: '1-10 июня 2023',
    location: 'Центральная библиотека'
  },
  {
    id: '4',
    title: 'Встреча с автором: Иван Петров',
    type: 'Встреча',
    time: '25 мая 2023, 17:00',
    location: 'Литературное кафе'
  },
  {
    id: '5',
    title: 'Детский конкурс чтецов',
    type: 'Конкурс',
    time: '5 июня 2023, 12:00',
    location: 'Библиотека №2',
    badge: 'Для детей'
  },
  {
    id: '6',
    title: 'Презентация новой книги',
    type: 'Презентация',
    time: '10 июня 2023, 19:00',
    location: 'Центральная библиотека'
  }
])

// Upcoming events (can be the same data for demo purposes)
const upcomingEvents = computed(() => events.value.slice(0, 5))

// Function to handle drag start
const startDrag = (e: MouseEvent | TouchEvent, slider: string) => {
  isDragging.value = true
  currentSlider.value = slider
  dragStartX.value =
    e.type === 'touchstart' ? (e as TouchEvent).touches[0].clientX : (e as MouseEvent).clientX
}

// Function to handle drag
const handleDrag = (e: MouseEvent | TouchEvent) => {
  if (!isDragging.value) return

  const currentX =
    e.type === 'touchmove' ? (e as TouchEvent).touches[0].clientX : (e as MouseEvent).clientX
  const diff = currentX - dragStartX.value

  if (Math.abs(diff) > 50) {
    if (diff > 0) {
      slidePrev(currentSlider.value as 'events')
    } else {
      slideNext(currentSlider.value as 'events')
    }
    endDrag()
  }
}

// Function to end drag
const endDrag = () => {
  isDragging.value = false
}

// Function to slide to the next item
const slideNext = (slider: 'events') => {
  if (slider === 'events') {
    const maxOffset = calculateMaxOffset('events', upcomingEvents.value.length)
    if (eventsSlideOffset.value < maxOffset) {
      eventsSlideOffset.value += 1
    }
  }
}

// Function to slide to the previous item
const slidePrev = (slider: 'events') => {
  if (slider === 'events' && eventsSlideOffset.value > 0) {
    eventsSlideOffset.value -= 1
  }
}

// Function to go to a specific slide
const goToSlide = (slider: string, index: number) => {
  if (slider === 'events') {
    eventsSlideOffset.value = index
  }
}

// Function to check if a slide is active
const isActiveSlide = (id: number, slider: string) => {
  if (slider === 'events') {
    const visibleStart = eventsSlideOffset.value
    const visibleEnd = visibleStart + eventsVisibleItems.value - 1
    const index = upcomingEvents.value.findIndex((event) => event.id === id)
    return index >= visibleStart && index <= visibleEnd
  }
  return false
}

// Function to calculate the maximum offset for a slider
const calculateMaxOffset = (slider: string, itemCount: number) => {
  if (slider === 'events') {
    return Math.max(0, itemCount - eventsVisibleItems.value)
  }
  return 0
}

// Setup and cleanup
onMounted(() => {
  window.addEventListener('mousemove', handleDrag)
  window.addEventListener('touchmove', handleDrag)
  window.addEventListener('mouseup', endDrag)
  window.addEventListener('touchend', endDrag)
})

onUnmounted(() => {
  window.removeEventListener('mousemove', handleDrag)
  window.removeEventListener('touchmove', handleDrag)
  window.removeEventListener('mouseup', endDrag)
  window.removeEventListener('touchend', endDrag)
})
</script>

